function getRandomDate(start, end) {
    const startDate = new Date(start).getTime();
    const endDate = new Date(end).getTime();
    const randomDate = new Date(Math.random() * (endDate - startDate) + startDate);
    return randomDate.toISOString().split('T')[0];
}

function getRandomPhoneNumber() {
    let randomRegion = generateRandomRegion();
    let randomLine = getRandomInRange(100_0000, 999_9999);
    return ` (${randomRegion}) ${randomLine}`;
}

function generateRandomRegion() {
    const excludedValue = 49;
    let randomNumber;

    do {
        randomNumber = getRandomInRange(10, 999);
    } while (randomNumber === excludedValue);

    return randomNumber;
}

function getRandomInRange(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

