function retryRequest(request, retries) {
    return new Promise((resolve, reject) => {
        return pm.sendRequest(request, async function (error, response) {
            let records = extractRecords(response);
            if (records?.length > 0) {
                return resolve(records);
            }
            else if (retries > 0) {
                await sleep();
                return resolve(retryRequest(request));
            }
            else
            {
                return reject(error);
            }
        });
    });
}

function extractRecords(response) {
    const records = (response?.json()?.records);
    return (records?.length > 0)
        ? records
        : [];
}

function sleep() {
    return new Promise(resolve =>
        setTimeout(resolve, delayInMilliseconds)
    );
}
